import { createReducer, on } from '@ngrx/store';
import { getAllUsers, getAllUsersSuccess, getSelectedUser } from '../actions/user.actions';
export const initialState = {
  AllUsers: [],
  selectedUser: {
    id: 0,
    gradGroupId: 0,
    firstName: "",
    lastName: "",
    email: "",
    type: -1,
    joinDate: new Date(),
    status: 1
  }
};
export const UserReducer = createReducer(initialState, on(getAllUsers, state => ({
  ...state,
  loading: true
})), on(getAllUsersSuccess, (state, {
  AllUsers
}) => ({
  ...state,
  AllUsers,
  loading: true
})), on(getSelectedUser, (state, {
  index,
  users
}) => ({
  ...state,
  selectedUser: users[index]
})));
