import { Component } from '@angular/core';
import { AccessPropertiesService } from 'src/app/services/access-properties.service';
import { Properties } from 'src/app/models/properties.interface';
import { CookieService } from 'ngx-cookie-service';
@Component({
  selector: 'app-employee-profile',
  templateUrl: './employee-profile.component.html',
  styleUrls: ['./employee-profile.component.css']
})
export class EmployeeProfileComponent {
  EmployeeFields: Properties[] = [];
  EditFields: Properties[] = [];

  isEdit: boolean = false;

  constructor(private accessPropertyService: AccessPropertiesService, private cookieService : CookieService) { }

  ngOnInit() {
    this.getEmployeeFields();
  }

  getEmployeeFields(){
    this.accessPropertyService.GetAccessProperties(this.cookieService.get('userEmail')).subscribe(
      data => {
        this.EmployeeFields = data;
      }
    );
  }

  toggleEdit() {
    this.isEdit = !this.isEdit;
    this.EditFields = JSON.parse(JSON.stringify(this.EmployeeFields));
  }

  canEdit(field: Properties): boolean{
    return this.isEdit && field.condition == 2;
  }

  captureChange(htmlValue: any, index: number) {
    this.EditFields[index].value = htmlValue.target.value;
  }

  saveChanges(){
    let payload : any[] = [];

    this.EditFields.forEach((field, index) =>{
      if(field.value !== this.EmployeeFields[index].value){
        payload.push({
          fieldId: field.id,
          value: field.value
        })
      }
    });
    this.accessPropertyService.UpdateProperties(this.cookieService.get('userEmail'), payload).subscribe(() => {
    });
    setTimeout(()=>{
      this.getEmployeeFields();
    },500)
    this.toggleEdit();
  }
}
<div class="row">
    <div *ngIf="!isEdit;else IsEditing">
        <button class="btn btn-primary" (click)="toggleEdit()">Edit</button>
    </div>
    <ng-template #IsEditing>
        <div class="row">
            <div class="col-auto">
                <button class="btn btn-success" (click)="saveChanges()">Save</button>
            </div>
            <div class="col-auto">
                <button class="btn btn-danger" (click)="toggleEdit()">Cancel</button>
            </div>
        </div>
    </ng-template>
</div>
<div class="row">
    <div *ngFor="let field of EmployeeFields; index as index" class="col-6">
        <div class="row">
            <div class="col-5">
                <label for={{field.label}}>{{field.label}}</label>
            </div>
            <div class="col-6">
                <div *ngIf="field.options.length > 0; else NoOptions">
                    <select (change)="captureChange($event, index)" [disabled]="!canEdit(field)">
                        <option [selected]="option == field.value" class="form-select" value={{option}} *ngFor="let option of field.options">{{option}}</option>
                    </select>
                </div>
                <ng-template #NoOptions>
                    <input [id]=field.label [readOnly]="!canEdit(field)" class="form-control" type={{field.type}} (change)="captureChange($event, index)" [value]=field.value>
                </ng-template>
            </div>
        </div>
    </div>
</div>
