import { createReducer, on } from '@ngrx/store';
import { GetLogin } from '../actions/login-in.actions';
export const initialState = {
  email: null,
  token: null,
  roles: null
};
export const LoginReducer = createReducer(initialState, on(GetLogin, (state, {
  payload
}) => ({
  ...state,
  email: payload.email,
  token: payload.token,
  type: payload.roles,
  loading: true
})));
