import { Component } from '@angular/core';
import { AuthService } from '@auth0/auth0-angular';
import { Store } from '@ngrx/store';
import { Token } from 'src/app/models/token.interface';
import { Observable } from 'rxjs';
import { CookieService } from 'ngx-cookie-service';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent {
  type$: Observable<Token> = this.store.select('app')
  selectedPage : string = this.cookieService.get("currentlPage") != "Dashboard" ?this.cookieService.get("currentlPage") :"Dashboard";

  constructor(
    private store: Store<{app: Token}>,
    private auth: AuthService,
    public cookieService: CookieService
    ) {}

  ngOnDestroy() {
    localStorage.removeItem('access_token');
    localStorage.removeItem('id_token');
    sessionStorage.removeItem('access_token');
    sessionStorage.removeItem('id_token');
  }

  Logout() {
    this.auth.logout({
      logoutParams: { returnTo: document.location.origin }
    });
  }

  ngOnInit() {
    this.selectedPage = this.cookieService.get('currentPage');
  }

  handleSelectedItem() {
    this.selectedPage = this.cookieService.get('currentPage');
  }
}
<div class="wrapper">
  <div class="sidebar" data-color="danger" data-background-color="white" data-image="./assets/img/sidebar-1.jpg">
    <app-sidebar ></app-sidebar>
    <div class="sidebar-background" style="background-image: url(./assets/img/sidebar-4.jpg)"></div>
  </div>
  <div class="main-panel">
    <app-header></app-header>
    <main class="main-content position-relative max-height-vh-100 h-100 border-radius-lg ps ps--active-y">
      <div *ngIf="cookieService.get('currentPage') === '' || cookieService.get('currentPage') === 'Dashboard'" #ShowDashBoard>
        <app-employee-profile></app-employee-profile>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'Add User'">
        <app-add-user></app-add-user>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'Charts'">
        <app-chart></app-chart>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'Edit Dashboard'">
        <app-create-charts></app-create-charts>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'Employee Manager'">
        <app-employee-role-manager></app-employee-role-manager>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'Role Manager'">
        <app-role-manager></app-role-manager>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'Manage Field'">
        <app-manage-field-code></app-manage-field-code>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'New Employee'">
        <app-new-employee></app-new-employee>
      </div>
      <div *ngIf="cookieService.get('currentPage') === 'View Employee'">
        <app-view-employee></app-view-employee>
      </div>
    </main>
  </div>
</div>
