import { Component, Output, EventEmitter } from '@angular/core';
import { EmployeeProfile } from 'src/app/models/employee-profile.interface';
import { EmployeeService } from 'src/app/services/employee.service';
import { CookieService } from 'ngx-cookie-service';


@Component({
  selector: 'app-view-employee',
  templateUrl: './view-employee.component.html',
  styleUrls: ['./view-employee.component.css']
})

export class ViewEmployeeComponent{
  @Output() selectedItem = new EventEmitter<{ selectedPage: string }>();
  Employees: EmployeeProfile[] = [];

  constructor(private employeeService: EmployeeService,
    private cookieService: CookieService) { }

  ngOnInit() {
    this.getEmployees();
  }

  getEmployees(){
    this.employeeService.getAllProfiles().subscribe(
      data => {
        this.Employees = data;
      }
    );
  }

  CaptureEvent(event: any) {
    const target = event.target as HTMLAnchorElement;
    this.cookieService.set('currentPage', target.innerText);
  }

  ViewUser(email: string){
    this.cookieService.set('selectedUser',email)
  }
}
<h5>Employee List</h5>
<h6>This page identifies as beautifully styled</h6>

<button (click)="CaptureEvent($event)" type="button" class="btn btn-primary">New Employee</button>

<div class="row">
  <div class="card">
    <div class="table-responsive">
      <table class="table align-items-center mb-0">
        <thead>
          <tr>
            <th>First Name</th>
            <th>Surname</th>
            <th>Telephone Number</th>
            <th>Employee Type</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let employee of Employees">
            <td>{{ employee.name }}</td>
            <td>{{ employee.surname }}</td>
            <td>{{ employee.cellphoneNo }}</td>
            <td>{{ employee.employeeType.name }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
